# Base64 encoded small ICO file for a stopped icon (red circle with X)
$stoppedIconBase64 = ""

# Base64 encoded small ICO file for a running icon (green circle with checkmark)
$runningIconBase64 = ""

# Base64 encoded small ICO file for an app icon (blue circle with three lines)
$appIconBase64 = ""

# Write the base64-encoded data to files
$stoppedIconBytes = [Convert]::FromBase64String($stoppedIconBase64)
[System.IO.File]::WriteAllBytes("resources\icons\syncthing_stopped.ico", $stoppedIconBytes)
Write-Host "Created stopped icon"

$runningIconBytes = [Convert]::FromBase64String($runningIconBase64)
[System.IO.File]::WriteAllBytes("resources\icons\syncthing_running.ico", $runningIconBytes)
Write-Host "Created running icon"

$appIconBytes = [Convert]::FromBase64String($appIconBase64)
[System.IO.File]::WriteAllBytes("resources\icons\syncthingers.ico", $appIconBytes)
Write-Host "Created app icon"

Write-Host "All icons created successfully!" 